import React from "react";
import styled from "styled-components";
import colors from "../colors";

const AboutSection = styled.section`
  padding: 6rem 10vw 4rem 10vw;
  background: ${colors.lightNavy};
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const SectionTitle = styled.h2`
  color: ${colors.green};
  font-size: 2rem;
  margin-bottom: 2rem;
  font-weight: 700;
`;

const AboutContent = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 3rem;
  width: 100%;
`;

const Bio = styled.div`
  flex: 2;
  color: ${colors.lightestSlate};
  font-size: 1.1rem;
  line-height: 1.7;
`;

const Skills = styled.div`
  flex: 1;
  min-width: 220px;
`;

const SkillsTitle = styled.h3`
  color: ${colors.slate};
  font-size: 1.1rem;
  margin-bottom: 1rem;
`;

const SkillsList = styled.ul`
  list-style: none;
  padding: 0;
  color: ${colors.green};
  font-size: 1rem;
`;

const skills = [
  "JavaScript (ES6+)",
  "React.js",
  "HTML & CSS",
  "Node.js",
  "Git & GitHub",
  "Figma",
  "Python (basic)",
];

function About() {
  return (
    <AboutSection id="about" data-aos="fade-up">
      <SectionTitle>About Me</SectionTitle>
      <AboutContent>
        <Bio>
          I'm an undergraduate BCA student at Kathford International College of
          Engineering and Management, passionate about web development and
          technology. I love building beautiful and functional web applications.{" "}
          <br />
          <br />I enjoy learning new technologies and collaborating on creative
          projects. My goal is to become a skilled full-stack developer and
          contribute to impactful digital solutions.
        </Bio>
        <Skills>
          <SkillsTitle>Skills</SkillsTitle>
          <SkillsList>
            {skills.map((skill) => (
              <li key={skill}>â€¢ {skill}</li>
            ))}
          </SkillsList>
        </Skills>
      </AboutContent>
    </AboutSection>
  );
}

export default About;
