import React from "react";
import styled from "styled-components";
import colors from "../colors";

const ProjectsSection = styled.section`
  padding: 6rem 10vw 4rem 10vw;
  background: ${colors.lightestNavy};
`;

const SectionTitle = styled.h2`
  color: ${colors.green};
  font-size: 2rem;
  margin-bottom: 2.5rem;
  font-weight: 700;
`;

const FeaturedProject = styled.div`
  display: flex;
  align-items: stretch;
  background: ${colors.lightNavy};
  border-radius: 16px;
  box-shadow: 0 4px 24px 0 rgba(10, 25, 47, 0.2);
  overflow: hidden;
  margin-bottom: 3rem;
  @media (max-width: 900px) {
    flex-direction: column;
  }
`;

const ProjectImageWrapper = styled.div`
  flex: 1.2;
  min-width: 0;
  background: ${colors.navy};
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ProjectImage = styled.img`
  width: 100%;
  height: 100%;
  max-height: 320px;
  object-fit: cover;
  border-radius: 0;
  @media (max-width: 900px) {
    max-height: 220px;
    border-radius: 0;
  }
`;

const ProjectInfo = styled.div`
  flex: 2;
  padding: 2.5rem 2rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  @media (max-width: 900px) {
    padding: 1.5rem 1rem;
  }
`;

const ProjectLabel = styled.span`
  color: ${colors.green};
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const ProjectTitle = styled.h3`
  color: ${colors.lightestSlate};
  font-size: 1.6rem;
  margin-bottom: 1rem;
`;

const ProjectDesc = styled.p`
  color: ${colors.lightSlate};
  font-size: 1.1rem;
  margin-bottom: 1.2rem;
`;

const ProjectTags = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.7rem;
  margin-bottom: 1.5rem;
`;

const Tag = styled.span`
  background: ${colors.navy};
  color: ${colors.green};
  font-size: 0.95rem;
  padding: 0.2rem 0.9rem;
  border-radius: 3px;
`;

const ProjectButton = styled.a`
  display: inline-block;
  background: transparent;
  color: ${colors.green};
  border: 1.5px solid ${colors.green};
  padding: 0.7rem 1.7rem;
  border-radius: 4px;
  font-size: 1rem;
  font-weight: 600;
  text-decoration: none;
  transition: background 0.2s, color 0.2s;
  &:hover {
    background: ${colors.green};
    color: ${colors.navy};
  }
`;

function Projects() {
  return (
    <ProjectsSection id="projects">
      <SectionTitle>Projects</SectionTitle>
      <FeaturedProject data-aos="fade-up">
        <ProjectImageWrapper>
          <ProjectImage
            src="/(elegance)home_page_ss.png"
            alt="E-commerce Website Screenshot"
          />
        </ProjectImageWrapper>
        <ProjectInfo>
          <ProjectLabel>Featured Project</ProjectLabel>
          <ProjectTitle>E-commerce Website</ProjectTitle>
          <ProjectDesc>
            A full-stack e-commerce platform with user authentication, product
            management, and a modern UI. Built as a college project.
          </ProjectDesc>
          <ProjectTags>
            <Tag>React</Tag>
            <Tag>Node.js</Tag>
            <Tag>MongoDB</Tag>
            <Tag>Express</Tag>
          </ProjectTags>
          {/* Optional: Add a link to GitHub or live demo */}
          {/* <ProjectButton href="#" target="_blank">View Project</ProjectButton> */}
        </ProjectInfo>
      </FeaturedProject>
    </ProjectsSection>
  );
}

export default Projects;
