import React from "react";
import styled from "styled-components";
import colors from "../colors";

const ProjectsSection = styled.section`
  padding: 6rem 10vw 4rem 10vw;
  background: ${colors.lightestNavy};
`;

const SectionTitle = styled.h2`
  color: ${colors.green};
  font-size: 2rem;
  margin-bottom: 2.5rem;
  font-weight: 700;
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  gap: 2.5rem;
`;

const ProjectCard = styled.div`
  background: ${colors.lightNavy};
  border-radius: 10px;
  box-shadow: 0 4px 24px 0 rgba(10, 25, 47, 0.2);
  padding: 2rem 1.5rem;
  display: flex;
  flex-direction: column;
  transition: transform 0.2s, box-shadow 0.2s;
  &:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 8px 32px 0 rgba(100, 255, 218, 0.15);
  }
`;

const ProjectTitle = styled.h3`
  color: ${colors.lightestSlate};
  font-size: 1.3rem;
  margin-bottom: 0.7rem;
`;

const ProjectDesc = styled.p`
  color: ${colors.lightSlate};
  font-size: 1rem;
  margin-bottom: 1.2rem;
`;

const ProjectTags = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.7rem;
`;

const Tag = styled.span`
  background: ${colors.navy};
  color: ${colors.green};
  font-size: 0.9rem;
  padding: 0.2rem 0.7rem;
  border-radius: 3px;
`;

const projects = [
  {
    title: "E-commerce Website",
    description:
      "A full-stack e-commerce platform with user authentication, product management, and a modern UI. Built as a college project.",
    tags: ["React", "Node.js", "MongoDB", "Express"],
  },
  // Add more projects here as you build them!
];

function Projects() {
  return (
    <ProjectsSection id="projects">
      <SectionTitle>Projects</SectionTitle>
      <ProjectsGrid>
        {projects.map((project) => (
          <ProjectCard key={project.title}>
            <ProjectTitle>{project.title}</ProjectTitle>
            <ProjectDesc>{project.description}</ProjectDesc>
            <ProjectTags>
              {project.tags.map((tag) => (
                <Tag key={tag}>{tag}</Tag>
              ))}
            </ProjectTags>
          </ProjectCard>
        ))}
      </ProjectsGrid>
    </ProjectsSection>
  );
}

export default Projects;
